        private void createDatabaseToolStripMenuItem_Click(object sender, EventArgs e)
        {
            if (DiversityWorkbench.Settings.ModuleName.Length == 0)
            {
                string Name = System.Reflection.Assembly.GetExecutingAssembly().FullName;
                Name = Name.Substring(0, Name.IndexOf(","));
                DiversityWorkbench.Settings.ModuleName = Name;
            }
            System.Collections.Generic.Dictionary<string, string> Versions = this.createDatabase_Versions();
            string Message = "";
            if (Versions.Count > 0)
            {
                if (DiversityWorkbench.Database.CreateDatabase(Versions, ref Message))
                {
                    this.setDatabase();
                    this.setUpdateMenu();
                    this.initQuery();
                }
                else
                {
                    if (Message.Length > 0)
                        MessageBox.Show(Message, "Failed to create database", MessageBoxButtons.OK, MessageBoxIcon.Error);
                    else
                        MessageBox.Show("No database was created");
                }
            }
            else
                MessageBox.Show("Installation script missing");
        }

        private System.Collections.Generic.Dictionary<string, string> createDatabase_Versions()
        {
            System.Collections.Generic.Dictionary<string, string> Versions = new Dictionary<string, string>();
            try
            {
                System.Resources.ResourceManager rm = Properties.Resources.ResourceManager;
                System.Resources.ResourceSet rs = rm.GetResourceSet(new System.Globalization.CultureInfo("en-US"), true, true);
                if (rs != null)
                {
                    System.Collections.IDictionaryEnumerator de = rs.GetEnumerator();
                    while (de.MoveNext() == true)
                    {
                        if (de.Entry.Value is string)
                        {
                            if (de.Key.ToString().StartsWith("DiversityCollectionUpdate_000000_to_"))
                            {
                                Versions.Add(de.Key.ToString(), de.Value.ToString());
                                break;
                            }
                        }
                    }
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            return Versions;
        }


        private void databaseToolStripMenuItem_Click(object sender, EventArgs e)
        {
            try
            {
                // #137
                bool DatabaseChanged = false;
                string CurrentConnectionString = DiversityWorkbench.Settings.ConnectionString;

                if (this.userControlQueryList.QueryConditionVisiblity.Length > 0)
                    DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.QueryConditionVisibility = this.userControlQueryList.QueryConditionVisiblity;
                DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.Save();

                DiversityWorkbench.Forms.FormConnectToDatabase f = new DiversityWorkbench.Forms.FormConnectToDatabase(DiversityWorkbench.Settings.ModuleName, createDatabase_Versions(), true);
                f.setHelpProviderNameSpace(DiversityWorkbench.WorkbenchResources.WorkbenchDirectory.HelpProviderNameSpace(), "Login");
                f.StartPosition = FormStartPosition.CenterParent;
                f.ShowDialog();
                if (f.DialogResult == DialogResult.OK)
                {
                    this.Cursor = System.Windows.Forms.Cursors.WaitCursor;
                    Forms.FormStarting fs = new Forms.FormStarting(DiversityCollection.Resource.Database, "Connecting to " + f.Database);
                    fs.MaximizeBox = false;
                    fs.MinimizeBox = false;
                    fs.setMax(26);
                    fs.Show();
                    this.splitContainerMain.Visible = false;
                    //this.splitContainerMain.SuspendLayout();
                    Application.DoEvents();

                    if (!this.setDatabase(fs))
                        this.userControlQueryList.QueryTable.Clear();

                    //#137
                    DatabaseChanged = CurrentConnectionString != DiversityWorkbench.Settings.ConnectionString;
                    if (DatabaseChanged)
                    {
                        this.userControlQueryList.setQueryConditions(this.CollectionSpecimen.QueryConditions(), DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.QueryConditionVisibility.ToString());
                        CacheDatabase.CacheDB.ResetConnectionStringCacheDB();
                        LookupTable.ResetLookupTables();
                        //DiversityWorkbench.Settings.ResetDefaultDatabaseAvailability();
                    }

                    this.initQuery(fs);
                    this.setConnectionMenu();
                    //this.splitContainerMain.Visible = true;
                    //this.splitContainerMain.ResumeLayout();
                    fs.Close();
                }
                else
                {
                    // nur Löschen wenn was da ist und die Verbindung ungültig
                    if (this.userControlQueryList.QueryTable.Rows.Count > 0
                        && DiversityWorkbench.Settings.ConnectionString.Length == 0)
                        this.userControlQueryList.QueryTable.Clear();
                    this.setConnectionMenu();
                }
                //this.setConnectionMenu();
            }
            catch (Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            this.splitContainerMain.Visible = true;
            this.Cursor = System.Windows.Forms.Cursors.Default;
        }

