        #region Timeout

        private enum TimeoutTarget { web, query, connection, start, none }
        private void setTimeoutTooltips(TimeoutTarget timeoutTarget = TimeoutTarget.none)
        {
            try
            {
                int iTweb = 0;
                int iTquery = 0;
                int iTcon = 0;
                int iTstart = 0;
                string Tweb = "";
                string Tquery = "";
                string Tcon = "";
                string Tstart = "";
                if (this._TimeoutSettingMethod.Length > 0)
                {
                    iTweb = this._TimeoutWeb;
                    iTquery = this._TimeoutDatebase;
                    iTcon = this._TimeoutConnection;
                    iTstart = this._TimeoutStart;
                }
                else
                {
                    iTweb = DiversityWorkbench.Settings.TimeoutWebInSeconds;
                    iTquery = DiversityWorkbench.Settings.TimeoutDatabase;
                    iTcon = DiversityWorkbench.Settings.TimeoutConnection;
                    iTstart = DiversityWorkbench.Settings.TimeoutStart;
                }
                Tweb = iTweb.ToString() + " sec.";
                if (iTweb == 0)
                    Tweb = "Do not search";
                Tquery = iTquery.ToString() + " sec.";
                if (iTquery == 0)
                    Tquery = "No limit";
                Tcon = iTcon.ToString() + " sec.";
                if (iTcon == 0)
                    Tcon = "No limit";
                Tstart = iTstart.ToString() + " sec.";
                if (iTstart == 0)
                    Tstart = "No limit";
                else if (iTstart < 0)
                    Tstart = "Use default timeouts for start";
                if (timeoutTarget != TimeoutTarget.none)
                {
                    switch (timeoutTarget)
                    {
                        case TimeoutTarget.connection:
                            timeoutForConnectionToolStripMenuItem.ToolTipText = Tcon;
                            break;
                        case TimeoutTarget.query:
                            timeoutForDatabaseToolStripMenuItem.ToolTipText = Tquery;
                            break;
                        case TimeoutTarget.start:
                            timeoutForStartToolStripMenuItem.ToolTipText = Tstart;
                            break;
                        case TimeoutTarget.web:
                            timeoutForWebresponseToolStripMenuItem.ToolTipText = Tweb;
                            break;
                    }
                }
                else
                {
                    timeoutForWebresponseToolStripMenuItem.ToolTipText = Tweb;
                    timeoutForDatabaseToolStripMenuItem.ToolTipText = Tquery;
                    timeoutForConnectionToolStripMenuItem.ToolTipText = Tcon;
                    timeoutForStartToolStripMenuItem.ToolTipText = Tstart;
                }
            }
            catch (Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
        }

