                switch (Target)
                {
                    case "Series":
                        ImportSteps = this.ImportStepsSeries();
                        break;
                    case "Event":
                        ImportSteps = this.ImportStepsEvent(true);
                        break;
                    case "Specimen":
                        ImportSteps = this.ImportStepsSpecimen(fs);
                        break;
                    case "Observation":
                        ImportSteps = this.ImportStepsObservation(fs);
                        break;
                    case "Analysis":
                        ImportSteps = this.ImportStepsAnalysis;
                        break;
                    case "Collection":
                        ImportSteps = this.ImportStepsCollection;
                        break;
                    case "Method":
                        ImportSteps = this.ImportStepsMethod;
                        break;
                    case "Processing":
                        ImportSteps = this.ImportStepsProcessing;
                        break;
                    case "Transaction":
                        ImportSteps = this.ImportStepsTransaction;
                        break;
                    case "Task":
                        ImportSteps = Tasks.Task.ImportStepsTask;
                        break;
                    case "CollectionTask":
                        ImportSteps = Tasks.CollectionTask.ImportStepsCollectionTask;
                        break;
                }
                if (ImportSteps == null)
                    ImportSteps = new Dictionary<string, DiversityWorkbench.Import.Step>();
                fs.ShowCurrentStep("Import steps for " + Target + " created. Opening import form\r\n||||||||..");
                DiversityWorkbench.Import.FormWizard f = new DiversityWorkbench.Import.FormWizard(Target, DiversityCollection.Properties.Settings.Default.DatabaseVersion, ImportSteps, this.helpProviderDiversityCollection.HelpNamespace, fs);
                System.Collections.Generic.Dictionary<DiversityWorkbench.Import.Import.SchemaFileSource, string> SFS = new Dictionary<DiversityWorkbench.Import.Import.SchemaFileSource, string>();
                SFS.Add(DiversityWorkbench.Import.Import.SchemaFileSource.SNSB, "https://github.com/SNSB/DWB-Contrib/tree/master/DiversityCollection/Import/Schemas");
                SFS.Add(DiversityWorkbench.Import.Import.SchemaFileSource.ZFMK, "https://github.com/ZFMK/Labels-and-Imports-for-DiversityWorkbench/tree/master/ImportSchedules");
                DiversityWorkbench.Import.iWizardInterface i = f;
                i.SetSourcesForSchemaFiles(SFS);

                f.Height = this.Height - 10;
                f.StartPosition = FormStartPosition.CenterParent;

                fs.Close();

                f.ShowDialog();
            }
