        private void cacheDatabaseToolStripMenuItem1_Click(object sender, EventArgs e)
        {
            try
            {
                if (DiversityWorkbench.Settings.ServerConnection.ConnectionIsValid)
                {
                    string BaseURL = DiversityWorkbench.Settings.ServerConnection.BaseURL;
                    if (BaseURL.ToLower() != DiversityWorkbench.Database.BaseURLaccordingToDatabaseName().ToLower() &&
                        !this._BaseURLmismatchAnnounced)
                    {
                        string Message = "The published address of the database does not correspond to the database name" +
                            "\r\n\r\nexpect:\t" + DiversityWorkbench.Database.BaseURLaccordingToDatabaseName() + " " +
                            "\r\nfound:\t" + DiversityWorkbench.Settings.ServerConnection.BaseURL + "  " +
                            "\r\n\r\nTo adjust the published address select\r\nAdministration - Database - Set published address...\r\nfrom the menu";
                        System.Windows.Forms.MessageBox.Show(Message, "Mismatch in published addresss", MessageBoxButtons.OK, MessageBoxIcon.Information);
                        this._BaseURLmismatchAnnounced = true;
                    }
                    this.Cursor = System.Windows.Forms.Cursors.WaitCursor;
                    try
                    {
                        using (var f = new DiversityCollection.CacheDatabase.FormCacheDatabase())
                        {
                            if (f.InitSuccessful && f.HasAccess) //#174
                            {
                                f.setHelp("Cache database");
                                f.Height = this.Height - 8;
                                f.Width = this.Width - 8;
                                f.ShowDialog();
                            }
                            else
                            {
                                System.Windows.Forms.MessageBox.Show("You have no access to the cache database\r\nPlease turn to your administrator", "No access", MessageBoxButtons.OK, MessageBoxIcon.Information); // #174
                            }
                        }
                    }
                    catch (System.Exception ex)
                    {
                        DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                        System.Windows.Forms.MessageBox.Show("An error occurred while caching the database. Please check the error log for details.", "Error", MessageBoxButtons.OK, MessageBoxIcon.Error);
                    }
                    finally
                    {
                        this.Cursor = System.Windows.Forms.Cursors.Default;
                    }
                }
                else
                {
                    System.Windows.Forms.MessageBox.Show("Please connect to a database");
                    return;
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
        }

        private bool _BaseURLmismatchAnnounced = false;

