            this.Cursor = System.Windows.Forms.Cursors.Default;
            f.ShowDialog();
            if (f.DialogResult == DialogResult.OK)
            {
                int CollectionID = f.ID;
                bool selectAll = false;
                string Message = "Transfer all " + this.userControlQueryList.ListOfIDs.Count.ToString() + " specimen into the collection " + f.DisplayText;
                if (this.userControlQueryList.ListOfSelectedIDs.Count < this.userControlQueryList.ListOfIDs.Count)
                {
                    string Ask = "Only " + this.userControlQueryList.ListOfSelectedIDs.Count.ToString() + " of " + this.userControlQueryList.ListOfIDs.Count.ToString() + " items is selected.\r\nDo you want to transfer all items in the list?";
                    switch (System.Windows.Forms.MessageBox.Show(Ask, "Transfer to collection " + f.DisplayText, MessageBoxButtons.YesNoCancel, MessageBoxIcon.Question))
                    {
                        case DialogResult.Yes:
                            selectAll = true;
                            break;
                        case DialogResult.No:
                            Message = "Transfer " + this.userControlQueryList.ListOfSelectedIDs.Count.ToString() + " selected specimen into the collection " + f.DisplayText;
                            break;
                        case DialogResult.Cancel:
                            return;
                            break;
                    }
                }
                //if (System.Windows.Forms.MessageBox.Show(Message, "Transfer to collection " + f.DisplayText, MessageBoxButtons.OKCancel) == System.Windows.Forms.DialogResult.OK)
                {
                    this.Cursor = System.Windows.Forms.Cursors.WaitCursor;
                    try
                    {
                        int Count = this.userControlQueryList.ListOfIDs.Count;
                        string IDs = "";
                        System.Collections.Generic.List<int> Items = new List<int>();
                        if (selectAll) Items = this.userControlQueryList.ListOfIDs;
                        else Items = this.userControlQueryList.ListOfSelectedIDs;
                        foreach (int i in Items)
                        {
                            if (IDs.Length > 0)
                                IDs += ", ";
                            IDs += i.ToString();
                        }
                        string SQL = "";
                        SQL = "UPDATE P SET CollectionID = " + f.ID.ToString() + " FROM CollectionSpecimenPart P WHERE P.CollectionSpecimenID IN (" + IDs + ")";
                        Message = "";
                        bool InsertFailed = false;
                        if (DiversityWorkbench.Forms.FormFunctions.SqlExecuteNonQuery(SQL, ref Message))
                        {
                            SQL = "SELECT COUNT(*) FROM CollectionSpecimen S LEFT OUTER JOIN CollectionSpecimenPart P ON S.CollectionSpecimenID = P.CollectionSpecimenID WHERE P.CollectionSpecimenID IS NULL AND S.CollectionSpecimenID IN (" + IDs + ")";
                            string Result = DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SQL);
                            if (Result != "0")
                            {
                                DiversityWorkbench.Forms.FormGetStringFromList fMat = new DiversityWorkbench.Forms.FormGetStringFromList(DiversityCollection.LookupTable.DtMaterialCategoryList, "Please select a material category", true);
                                fMat.ShowDialog();
                                if (fMat.DialogResult == System.Windows.Forms.DialogResult.OK)
                                {
                                    string MatCat = fMat.SelectedString;
                                    SQL = "INSERT INTO CollectionSpecimenPart (CollectionSpecimenID, CollectionID, StorageLocation, MaterialCategory) " +
                                        "SELECT S.CollectionSpecimenID, " + CollectionID.ToString() + ", U.LastIdentificationCache, '" + MatCat + "' " +
                                        "FROM CollectionSpecimen S " +
                                        "LEFT OUTER JOIN CollectionSpecimenPart P ON S.CollectionSpecimenID = P.CollectionSpecimenID " +
                                        "LEFT OUTER JOIN IdentificationUnit U ON U.CollectionSpecimenID = S.CollectionSpecimenID " +
                                        "WHERE P.CollectionSpecimenID IS NULL AND S.CollectionSpecimenID IN (" + IDs + ") " +
                                        "AND EXISTS (SELECT * FROM IdentificationUnit M WHERE M.CollectionSpecimenID = U.CollectionSpecimenID GROUP BY M.CollectionSpecimenID HAVING U.DisplayOrder = MIN(M.DisplayOrder)) ";
                                    string MessageAdd = "";
                                    if (!DiversityWorkbench.Forms.FormFunctions.SqlExecuteNonQuery(SQL, ref MessageAdd))
                                    {
                                        Message += "\r\nFailed to insert " + Result + " parts.\r\n" + MessageAdd;
                                        InsertFailed = true;
                                        int iFailed = int.Parse(Result);
                                        Count -= iFailed;
                                    }
                                    else
                                    {
                                        SQL = "INSERT INTO IdentificationUnitInPart (CollectionSpecimenID, IdentificationUnitID, SpecimenPartID, DisplayOrder) " +
                                            "SELECT S.CollectionSpecimenID, U.IdentificationUnitID, P.SpecimenPartID, U.DisplayOrder " +
                                            "FROM CollectionSpecimen AS S INNER JOIN " +
                                            "CollectionSpecimenPart AS P ON S.CollectionSpecimenID = P.CollectionSpecimenID INNER JOIN " +
                                            "IdentificationUnit AS U ON U.CollectionSpecimenID = S.CollectionSpecimenID " +
                                            "WHERE (S.CollectionSpecimenID IN (" + IDs + "))  " +
                                            "AND (NOT EXISTS (SELECT * FROM IdentificationUnitInPart AS M " +
                                            "WHERE (CollectionSpecimenID = U.CollectionSpecimenID) AND (IdentificationUnitID = U.IdentificationUnitID) AND (SpecimenPartID = P.SpecimenPartID)))";
                                        DiversityWorkbench.Forms.FormFunctions.SqlExecuteNonQuery(SQL);
                                    }
                                }
                            }
                            string FinalMessage = Count.ToString() + " specimen transferred into the collection " + f.DisplayText;
                            if (InsertFailed)
                                FinalMessage += "\r\n" + Message;
                            System.Windows.Forms.MessageBox.Show(FinalMessage);
                            this.saveSpecimen();
                        }
                        else
                            System.Windows.Forms.MessageBox.Show("Transfer failed: " + Message);
                    }
                    catch (Exception ex)
                    {
                        DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                    }
                    this.Cursor = System.Windows.Forms.Cursors.Default;
                }
            }
        }

