        private void transferTransactionToolStripMenuItem_Click(object sender, EventArgs e)
        {
            //DiversityCollection.Forms.FormTransaction f = new FormTransaction("WHERE TransactionType NOT IN ('return', 'forwarding')", "Transfer to transaction");
            DiversityCollection.Forms.FormTransaction f = new FormTransaction(true);
            f.ShowDialog();
            if (f.DialogResult == DialogResult.OK)
            {
                this.Cursor = System.Windows.Forms.Cursors.WaitCursor;
                int TransactionID = f.ID;
                string Transaction = f.TransactionTitle;
                string TransactionType = f.TypeOfTransaction;
                string TransactionTitle = f.TransactionTitle;

                // Checking for a collection
                int? CollectionID = null;
                DiversityWorkbench.Forms.FormGetItemFromHierarchy fColl = new DiversityWorkbench.Forms.FormGetItemFromHierarchy(DiversityCollection.LookupTable.DtCollectionWithHierarchy, "CollectionID", "CollectionParentID", "CollectionName", "CollectionID", "Do you want to restrict the transfer to a certain collection?", "Restrict to collection?");
                fColl.ShowDialog();
                int CID;
                if (fColl.DialogResult == System.Windows.Forms.DialogResult.OK && fColl.SelectedValue.Length > 0)
                {
                    if (int.TryParse(fColl.SelectedValue, out CID))
                        CollectionID = CID;
                }

                // Checking for a material category
                string MatCat = "";
                System.Data.DataTable dtMat = DiversityWorkbench.EnumTable.EnumTableForQuery("CollMaterialCategory_Enum", false, false, true);
                DiversityWorkbench.Forms.FormGetStringFromList fMat = new DiversityWorkbench.Forms.FormGetStringFromList(dtMat, "DisplayText", "Code", "Restrict to material?", "Do you want to restrict the transfer to the transaction to a material category?");
                fMat.ShowDialog();
                if (fMat.DialogResult == System.Windows.Forms.DialogResult.OK)
                    MatCat = fMat.SelectedValue;
                int iFailedReturns = 0;
                List<string> noEventSpecimenIds = new List<string>();
                List<string> noEventRegulationSpecimenIds = new List<string>();
                int addRegulationToEvent = 3;
                int i = 0;
                try
                {
                    foreach (int ID in this.userControlQueryList.ListOfIDs)
                    {
                        if (TransactionType == "return" || TransactionType == "forwarding")
                        {
                            string SqlReturn = "SELECT COUNT(*) " +
                                "FROM CollectionSpecimenTransaction CL, [Transaction] TL,  [Transaction] TR " +
                                "WHERE TR.TransactionID = " + TransactionID.ToString() + " " +
                                "AND TL.TransactionType = 'loan' " +
                                "AND CL.TransactionID = TL.TransactionID " +
                                "AND CL.CollectionSpecimenID = " + ID.ToString() + " " +
                                "AND TR.ParentTransactionID = TL.TransactionID ";
                            if (DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SqlReturn) == "0")
                            {
                                iFailedReturns++;
                                continue;
                            }
                        }

                        if (TransactionType == "regulation")
                        {
                            string sqlGetEvent =
                                "SELECT count(CollectionEventID) from CollectionSpecimen where CollectionSpecimenID = " +
                                ID.ToString();

                            if (DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(sqlGetEvent) == "0")
                            {
                                if (System.Windows.Forms.MessageBox.Show(
                                        "The SpecimenID has no event. To add a regulation to a specimen part, you need an event and add the regulation to the event.",
                                        "Add Regulation", MessageBoxButtons.OK) ==
                                    System.Windows.Forms.DialogResult.OK)
                                {
                                    noEventSpecimenIds.Add(ID.ToString());
                                    continue;
                                }

                            }
                            string SqlRegulation = "SELECT Count(*) From CollectionEventRegulation CR " +
                                               "WHERE CR.TransactionID = " + TransactionID.ToString() + " " +
                                               "AND CR.CollectionEventID = (SELECT CollectionEventID from CollectionSpecimen where CollectionSpecimenID = " +
                                               ID.ToString() + ")";

                            if (DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SqlRegulation) == "0")
                            {
                                //iFailedReturns++;
                                //continue
                                if (addRegulationToEvent == 3)
                                {
                                    if (System.Windows.Forms.MessageBox.Show(
                                            "The regulation is not yet part of all events. Would you like to add the regulation to the events?",
                                            "Add Regulation", MessageBoxButtons.YesNo) ==
                                        System.Windows.Forms.DialogResult.Yes)
                                    {
                                        addRegulationToEvent = 1;
                                    }
                                    else
                                    {
                                        addRegulationToEvent = 0;
                                    }
                                }

                                if (addRegulationToEvent == 1)
                                {
                                    string insertSQL = "BEGIN TRY BEGIN TRANSACTION TR " +
                                                       "INSERT INTO CollectionEventRegulation" +
                                                       "(CollectionEventID, Regulation, TransactionID) " +
                                                       "SELECT cs.CollectionEventID, tr.TransactionTitle, tr.TransactionID " +
                                                       "FROM CollectionSpecimen cs, TransactionRegulation tr " +
                                                       "WHERE (cs.CollectionSpecimenID = " + ID.ToString() + ") " +
                                                       "AND (tr.TransactionID = " + TransactionID.ToString() + ") "
                                                       + " COMMIT TRANSACTION TR END TRY BEGIN CATCH ROLLBACK TRANSACTION TR END CATCH ";
                                    if (!DiversityWorkbench.Forms.FormFunctions.SqlExecuteNonQuery(insertSQL))
                                    {
                                        noEventRegulationSpecimenIds.Add(ID.ToString());
                                        continue;
                                    }
                                }
                                else
                                {
                                    noEventRegulationSpecimenIds.Add(ID.ToString());
                                    continue;
                                }
                            }

                        }
                        string SqlSource = "FROM CollectionSpecimenPart AS P " +
                            "WHERE (CollectionSpecimenID = " + ID.ToString() + ") AND (NOT EXISTS " +
                            "(SELECT CollectionSpecimenID " +
                            "FROM CollectionSpecimenTransaction AS T " +
                            "WHERE (CollectionSpecimenID = " + ID.ToString() + ") AND (TransactionID = " + TransactionID.ToString() + ") " +
                            "AND T.SpecimenPartID = P.SpecimenPartID))";
                        if (CollectionID != null)
                            SqlSource += " AND P.CollectionID = " + CollectionID.ToString();
                        if (MatCat.Length > 0)
                            SqlSource += " AND P.MaterialCategory = '" + MatCat + "'";
                        string SqlTest = "SELECT COUNT(*) " + SqlSource;
                        int iParts = 0;

                        if (int.TryParse(DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SqlTest), out iParts) && iParts > 0)
                        {
                            i += iParts;
                            string SQL = "";
                            if (TransactionType == "regulation")
                            {
                                SQL += "INSERT INTO CollectionSpecimenTransaction " +
                                "(CollectionSpecimenID, TransactionID, SpecimenPartID, AccessionNumber, TransactionTitle) " +
                                "SELECT  CollectionSpecimenID, " + TransactionID.ToString() + " AS TransactionID, SpecimenPartID, AccessionNumber, '" + TransactionTitle.ToString() + "' AS TransactionTitle " +
                                SqlSource + "; ";
                            }
                            else
                            {
                                SQL += "INSERT INTO CollectionSpecimenTransaction " +
                                "(CollectionSpecimenID, TransactionID, SpecimenPartID, IsOnLoan, AccessionNumber, TransactionTitle) " +
                                "SELECT  CollectionSpecimenID, " + TransactionID.ToString() + " AS TransactionID, SpecimenPartID, 1 AS IsOnLoan, AccessionNumber, '" + TransactionTitle.ToString() + "' AS TransactionTitle " +
                                SqlSource + "; ";
                            }
                            if (TransactionType == "return" || TransactionType == "forwarding")
                            {
                                SQL = "BEGIN TRY BEGIN TRANSACTION TR "
                                    + SQL
                                    + " UPDATE CL SET CL.TransactionReturnID = " + TransactionID.ToString() + " " +
                                    "FROM CollectionSpecimenTransaction CL, [Transaction] TL,  [Transaction] TR " +
                                    "WHERE TR.TransactionID = " + TransactionID.ToString() + " " +
                                    "AND TL.TransactionType = 'loan' " +
                                    "AND CL.TransactionID = TL.TransactionID " +
                                    "AND CL.CollectionSpecimenID = " + ID.ToString() + " " +
                                    "AND TR.ParentTransactionID = TL.TransactionID "
                                    + " COMMIT TRANSACTION TR END TRY BEGIN CATCH ROLLBACK TRANSACTION TR END CATCH ";
                            }

                            if (!DiversityWorkbench.Forms.FormFunctions.SqlExecuteNonQuery(SQL))
                            {
                                iFailedReturns++;
                            }
                        }
                    }
                }
                catch (Exception ex)
                {
                    System.Windows.Forms.MessageBox.Show(ex.Message);
                }
                this.Cursor = System.Windows.Forms.Cursors.Default;
                string Message = "";
                if (TransactionType == "regulation" && iFailedReturns != 0)
                    Message += iFailedReturns.ToString() + " parts could not be included for the " + TransactionType;
                else
                    Message += i.ToString() + " parts of the selected specimens";

                if (CollectionID != null)
                    Message += " within collection " + fColl.SelectedString;
                if (MatCat.Length > 0)
                    Message += " of the material category " + MatCat;
                if (iFailedReturns == 0)
                    Message += " are now included in the transaction\r\n" + Transaction;
                if ((TransactionType == "return" || TransactionType == "forwarding") && iFailedReturns > 0)
                    Message += "\r\n\r\n" + iFailedReturns.ToString() + " parts could not be included because no matching loan was found for the " + TransactionType;
                if (TransactionType == "regulation" && noEventSpecimenIds.Count > 0)
                {
                    Message +=
                        "\r\n\r\n As the following species do not have an event, the regulation could not be added: ";
                    foreach (string t in noEventSpecimenIds)
                        Message += "\r\n " + t;
                }

                if (TransactionType == "regulation" && noEventRegulationSpecimenIds.Count > 0)
                {
                    Message +=
                        "\r\n\r\n The regulation could not be added for the following species because the regulation is missing for the corresponding events: ";
                    foreach (string t in noEventRegulationSpecimenIds)
                        Message += "\r\n " + t;
                }


                System.Windows.Forms.MessageBox.Show(Message);
                if (i > 0)
                    this.saveSpecimen();
            }
        }

