        private void enum_series_descriptorToolStripMenuItem_Click(object sender, EventArgs e)
        {
            //bool OK = this.EditEnumeration(enum_series_descriptorToolStripMenuItem, "CollEventSeriesDescriptorType_Enum", "type of collection event series descriptor");

            try
            {
                System.Collections.Generic.Dictionary<string, System.Drawing.Image> images = new Dictionary<string, Image>();
                images.Add("", DiversityCollection.Resource.NULL);
                images.Add("Descriptor", DiversityCollection.Resource.KeyBlue);
                images.Add("DiversityAgents", DiversityCollection.Resource.Agent);
                images.Add("DiversityCollection", DiversityCollection.Resource.Collection);
                images.Add("DiversityGazetteer", DiversityCollection.Resource.Country);
                images.Add("DiversityProjects", DiversityCollection.Resource.Project1);
                images.Add("DiversityReferences", DiversityCollection.Resource.References);
                images.Add("DiversitySamplingPlots", DiversityCollection.Resource.SamplingPlot);
                images.Add("DiversityScientificTerms", DiversityCollection.Resource.Kristall);
                images.Add("DiversityTaxonNames", DiversityCollection.Resource.Plant);
                foreach (System.Collections.Generic.KeyValuePair<string, System.Drawing.Image> kv in DiversityWorkbench.CollectionSpecimen.DescriptorTypeImageDict)
                {
                    if (!images.ContainsKey(kv.Key))
                        images.Add(kv.Key, kv.Value);
                }

                DiversityWorkbench.Forms.FormEnumAdministration f = new DiversityWorkbench.Forms.FormEnumAdministration(
                    DiversityCollection.Resource.KeyBlue,
                    "CollEventSeriesDescriptorType_Enum",
                    "Administration of Descriptor types",
                    "", images);
                f.HierarchyChangesEnabled = true;
                f.ShowImage = true;
                f.setHelp("Descriptor");
                string SQL = "SELECT DescriptorType " +
                    "FROM CollectionEventSeriesDescriptor " +
                    "WHERE(URL <> '') " +
                    "GROUP BY DescriptorType " +
                    "HAVING(DescriptorType <> N'')";

                System.Data.DataTable dt = new DataTable();
                DiversityWorkbench.Forms.FormFunctions.SqlFillTable(SQL, ref dt);
                if (dt.Rows.Count > 0)
                {
                    System.Collections.Generic.List<string> Blocked = new List<string>();
                    foreach (System.Data.DataRow R in dt.Rows)
                        Blocked.Add(R[0].ToString());
                    f.setBlockedModuleTypes(Blocked);
                }

                SQL = "SELECT DescriptorType " +
                    "FROM CollectionEventSeriesDescriptor " +
                    "GROUP BY DescriptorType " +
                    "HAVING(DescriptorType <> N'')";
                dt.Clear();
                DiversityWorkbench.Forms.FormFunctions.SqlFillTable(SQL, ref dt);
                if (dt.Rows.Count > 0)
                {
                    System.Collections.Generic.List<string> Blocked = new List<string>();
                    foreach (System.Data.DataRow R in dt.Rows)
                        Blocked.Add(R[0].ToString());
                    f.setDoNotDelete(Blocked);
                }
                f.ShowDialog();
                if (f.DataHaveBeenChanged)
                {
                    //this._EventSeriesDescriptorType_Enum = null;
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }


        }

