            bool UpdateClient = false;
            if (DiversityWorkbench.Settings.ConnectionString.Length > 0)
            {
                Microsoft.Data.SqlClient.SqlConnection con = new Microsoft.Data.SqlClient.SqlConnection(DiversityWorkbench.Settings.ConnectionString);
                Microsoft.Data.SqlClient.SqlCommand C = new Microsoft.Data.SqlClient.SqlCommand(SQL, con);
                try
                {
                    con.Open();
                    User = C.ExecuteScalar()?.ToString() ?? string.Empty;
                    con.Close();
                    bool IsOwner = false;
                    if (DiversityWorkbench.Database.DatabaseRoles().Contains("db_owner"))
                        IsOwner = true;
                    if (User == "dbo" || IsOwner)
                    {
                        UpdateDatabaseNeeded = this.UpdateCheckForDatabase();
                        this.updateDatabaseToolStripMenuItem.Visible = UpdateDatabaseNeeded;
                        if (UpdateDatabaseNeeded)
                            MessageBox.Show(
                                "Please note:\r\nTo use this version of the software, you should update your database.\r\n " +
                                "To do this, click on the red update button in the menu bar.\r\n",
                                "DB Update Information", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                    }
                    else
                    {
                        this.updateDatabaseToolStripMenuItem.Visible = false;
                        if (this.UpdateCheckForDatabase())
                            MessageBox.Show(
                                "Please note:\r\nTo use this version of the software, the database needs an update.\r\n\r\n " +
                                "To do this, click on the red update button in the menu bar.\r\n\r\n " +
                                "If you do not have sufficient rights, ask your database administrator \r\n" +
                                "or use an older version of the software.\r\n " +
                                "Be aware that otherwise, unexpected errors may occur.",
                                "DB Update Information", MessageBoxButtons.OK, MessageBoxIcon.Warning);
                    }
                    string Version = "";
                    UpdateClient = this.UpdateCheckForClient(ref Version);
                    //UpdateClient = true;
                    this.updateClientToolStripMenuItem.Visible = UpdateClient;
                    //this._ClientUpToDate = !UpdateClient;
                    if (UpdateClient)
                        this.updateClientToolStripMenuItem.Text = "Download client version " + Version;

                    if (UpdateClient || UpdateDatabaseNeeded)
                        this.updateToolStripMenuItem.Visible = true;
                    else this.updateToolStripMenuItem.Visible = false;
                }
                catch { }
            }
