        private void PreSelectTreeNodeTableSequence(ref System.Collections.Generic.SortedList<float, string> TableList, string TableName, System.Data.DataTable Table)
        {
            string WhereClause = "";
            try
            {
                if (Table.Rows.Count > 1 && DiversityWorkbench.UserControls.UserControlQueryList.TableAliases.ContainsKey(TableName))
                {
                    if (DiversityWorkbench.UserControls.UserControlQueryList.TableQueryConditions[TableName].Count > 0)
                    {
                        foreach (DiversityWorkbench.IUserControlQueryCondition Q in DiversityWorkbench.UserControls.UserControlQueryList.TableQueryConditions[TableName])
                        {
                            bool IsAddedSetControl = false;
                            bool IsSetControl = false;
                            if (DiversityWorkbench.UserControls.UserControlQueryList.QueryConditionSets.ContainsKey(Q.Condition().Table) &&
                                DiversityWorkbench.UserControls.UserControlQueryList.QueryConditionSets[Q.Condition().Table].ContainsKey(Q.Condition().Column))
                            {
                                DiversityWorkbench.UserControls.UserControlQueryConditionSet S = (DiversityWorkbench.UserControls.UserControlQueryConditionSet)Q;
                                if (DiversityWorkbench.UserControls.UserControlQueryList.QueryConditionSets[Q.Condition().Table][Q.Condition().Column][0] != S)
                                {
                                    IsAddedSetControl = true;
                                }
                                IsSetControl = true;
                            }
                            if (IsAddedSetControl)
                                continue;

                            string Where = "";
                            if (IsSetControl)
                            {
                                Where = Q.Condition().Column + " = " + Q.WhereClause();
                                if (Where.IndexOf(".[") > -1)
                                    Where = Where.Substring(Where.IndexOf(".[") + 1);
                            }
                            else if (Q.Condition().Operator == "+")
                            {
                                string wc = Q.WhereClause();
                            }
                            else if (Q.Condition().CheckIfDataExist == DiversityWorkbench.QueryCondition.CheckDataExistence.DatasetsInRelatedTable)
                            {
                                // Markus 16.09.22 - bugfix - wurde nicht verwendet fuer count
                                string wc = Q.WhereClause();
                                if (wc.Length > 0 && wc.IndexOf(" AND ") > -1 && wc.StartsWith(" AND "))
                                    Where = wc.Substring(wc.IndexOf(" AND ") + "AND ".Length);
                                else if (Q.Condition().QueryType == DiversityWorkbench.QueryCondition.QueryTypes.Count)
                                    Where = wc;
                            }
                            else
                                Where = Q.WhereClause();
                            Where = Where.Replace(DiversityWorkbench.UserControls.UserControlQueryList.TableAliases[TableName] + ".", "");
                            Where = Where.Replace("[", "").Replace("]", "");
                            if (WhereClause.Length > 0) WhereClause += " AND ";
                            WhereClause += Where;
                        }
                        WhereClause = WhereClause.Replace(" N'", " '");
                        // Markus 4.8.22: funktioniert nicht mit IN (SELECT ...)
                        if (WhereClause.IndexOf(" IN (SELECT ") == -1)
                        {
                            // Markus 11.6.24: bei manchen Tabellen fehlen Spalten (e.g. Geography in CollectionEventLocalisation in DC)
                            try
                            {
                                System.Data.DataRow[] RR = Table.Select(WhereClause, "");
                                if (RR.Length > 0)
                                {
                                    float Sq = (float)RR.Length / Table.Rows.Count;
                                    while (TableList.ContainsKey(Sq))
                                        Sq += (float)0.00001;
                                    TableList.Add(Sq, TableName);
                                }
                            }
                            catch { }
                        }
                    }
                }
                else
                {
                    if (!TableList.ContainsValue(TableName))
                    {
                        switch (TableName)
                        {
                            case "CollectionProject":
                            case "CollectionSpecimen_Core2":
                                TableList.Add(14, "CollectionSpecimen_Core2");
                                break;
                            case "CollectionEventImage":
                                TableList.Add(13, "CollectionEvent");
                                break;
                            case "CollectionEvent":
                                TableList.Add(12, TableName);
                                break;
                            case "CollectionAgent_Core":
                                TableList.Add(11, TableName);
                                break;
                        }
                    }
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex, WhereClause);
            }
        }

        private bool PreSelectTreeNode(string TableName, System.Data.DataTable Table, System.Windows.Forms.TreeView TreeView)
        {
            try
            {
                if (DiversityWorkbench.UserControls.UserControlQueryList.TableAliases.ContainsKey(TableName) &&
                    DiversityWorkbench.UserControls.UserControlQueryList.TableQueryConditions.ContainsKey(TableName))
                {
                    if (DiversityWorkbench.UserControls.UserControlQueryList.TableQueryConditions[TableName].Count > 0)
                    {
                        string WhereClause = "";
                        System.Collections.Generic.List<string> Columns = new List<string>();
                        foreach (DiversityWorkbench.IUserControlQueryCondition Q in DiversityWorkbench.UserControls.UserControlQueryList.TableQueryConditions[TableName])
                        {
                            /// Markus 4.10.2022: Avoid search for missing column
                            if (!Table.Columns.Contains(Q.Condition().Column))
                                continue;

                            if (Q.Condition().IsSet)
                            {
                                if (Q.Condition().SetCount > 1)
                                    continue;
                                if (DiversityWorkbench.UserControls.UserControlQueryList.QueryConditionSetTableAliases.Count > 0)
                                {
                                    int i = 0;
                                    bool IsFirst = true;
                                    foreach (System.Collections.Generic.KeyValuePair<DiversityWorkbench.UserControls.UserControlQueryConditionSet, string> KV in DiversityWorkbench.UserControls.UserControlQueryList.QueryConditionSetTableAliases)
                                    {
                                        if (Q == KV.Key && i > 0)
                                            IsFirst = false;
                                        i++;
                                    }
                                    if (!IsFirst)
                                        continue;
                                }
                                //string Alias = DiversityWorkbench.UserControls.UserControlQueryList.TableAliases[TableName];
                                //if ()
                            }
                            string Where = Q.WhereClause();
                            // Markus 7.11.22: BETWEEN not possible for row filter
                            if (Where.IndexOf("BETWEEN") > -1)
                            {

                            }
                            string AndSeparator = " AND ";
                            if (Q.Condition().CheckIfDataExist == DiversityWorkbench.QueryCondition.CheckDataExistence.DatasetsInRelatedTable && Where.Trim().StartsWith("= ") && Where.IndexOf(AndSeparator) > -1)
                                Where = Where.Substring(Where.IndexOf(AndSeparator) + AndSeparator.Length);
                            Where = Where.Replace(DiversityWorkbench.UserControls.UserControlQueryList.TableAliases[TableName] + ".", "");
                            Where = Where.Replace("[", "").Replace("]", "");
                            if (WhereClause.Length > 0) WhereClause += AndSeparator;
                            WhereClause += Where;
                            Columns.Add(Q.Condition().Column);
                        }
                        if (WhereClause.Length > 0)
                            WhereClause = WhereClause.Replace(" N'", " '");
                        System.Data.DataRow[] RR = Table.Select(WhereClause, "");
                        if (RR.Length > 0)
                        {
                            System.Collections.Generic.List<System.Windows.Forms.TreeNode> Nodes = new List<TreeNode>();
                            this.getOverviewHierarchyNodes(null, Table.TableName, TreeView, ref Nodes);
                            foreach (System.Windows.Forms.TreeNode N in Nodes)
                            {
                                if (N.Tag == RR[0])
                                {
                                    N.EnsureVisible();
                                    TreeView.SelectedNode = N;
                                    return true;
                                }
                            }
                        }
                    }
                }
                else if (TableName == "CollectionSpecimen_Core2")
                {
                    System.Collections.Generic.List<System.Windows.Forms.TreeNode> Nodes = new List<TreeNode>();
                    this.getOverviewHierarchyNodes(null, Table.TableName, TreeView, ref Nodes);
                    if (Nodes.Count > 0)
                    {
                        //Nodes[0].EnsureVisible();
                        TreeView.SelectedNode = Nodes[0];
                    }
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            return false;
        }

