        private void CopyEvent(int CollectionSpecimenID, string IncludedTables)
        {
            string SQL = "execute dbo.procCopyCollectionSpecimen2 NULL , " + CollectionSpecimenID.ToString() + ", '', 2, '" + IncludedTables + "'";
            DiversityWorkbench.Forms.FormFunctions.SqlExecuteNonQuery(SQL);
