        private System.Collections.Generic.List<int> _ProjectIDsForPermissions;
        private System.Collections.Generic.List<int> ProjectIDsForPermissions(int CollectionSpecimenID)
        {
            if (_ProjectIDsForPermissions == null) _ProjectIDsForPermissions = new List<int>();
            _ProjectIDsForPermissions.Clear();
            string SQL = "SELECT C.ProjectID " +
                "FROM CollectionProject AS C INNER JOIN " +
                "ProjectList AS P ON C.ProjectID = P.ProjectID " +
                "WHERE (C.CollectionSpecimenID = " + CollectionSpecimenID.ToString() + ") ";
            System.Data.DataTable dt = new DataTable();
            DiversityWorkbench.Forms.FormFunctions.SqlFillTable(SQL, ref dt);
            foreach (System.Data.DataRow R in dt.Rows)
            {
                int ID;
                if (int.TryParse(R[0].ToString(), out ID))
                    _ProjectIDsForPermissions.Add(ID);
            }
            return _ProjectIDsForPermissions;
        }
        private System.Collections.Generic.List<int> _PreviousProjectIDs;

        private bool ProjectListChanged(int CollectionSpecimenID)
        {
            // Startup - no changes needed
            if (ID == -1 && CollectionSpecimenID == -1)
                return false;

            bool Change = false;
            try
            {
                if (CollectionSpecimenID != -1)
                {
                    if (this._PreviousProjectIDs == null)
                    {
                        this._PreviousProjectIDs = ProjectIDsForPermissions(CollectionSpecimenID);
                        return true;
                    }
                    else
                    {
                        System.Collections.Generic.List<int> PreviousProjectIDs = new List<int>();
                        foreach (int ID in this._PreviousProjectIDs) { PreviousProjectIDs.Add(ID); }

                        System.Collections.Generic.List<int> CurrentProjectIDs = this.ProjectIDsForPermissions(CollectionSpecimenID);
                        if (PreviousProjectIDs != null && PreviousProjectIDs.Count != CurrentProjectIDs.Count)
                        {
                            return true;
                        }
                        else if (PreviousProjectIDs != null)
                        {
                            foreach (int id in PreviousProjectIDs)
                            {
                                if (!CurrentProjectIDs.Contains(id))
                                {
                                    return true;
                                }
                            }
                        }
                    }
                }
            }
            catch (System.Exception ex) { DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex); }
            return Change;
        }

