        #region Intermediate Change of timeout

        private int _TimeoutStart;
        private int _TimeoutDatebase;
        private int _TimeoutConnection;
        private int _TimeoutWeb;
        private string _TimeoutSettingMethod = "";

        private void setTimeoutForLoginAndStart(string Method, bool Finish = false)
        {
            try
            {
                if (DiversityWorkbench.Settings.TimeoutStart < 0)
                    return;

                if (_TimeoutSettingMethod.Length == 0)
                    _TimeoutSettingMethod = Method;
                // this part is called at the end of the process when the original values of the timeouts should be restored
                if (Finish && _TimeoutSettingMethod == Method)
                {
                    DiversityWorkbench.Settings.TimeoutDatabase = _TimeoutDatebase;
                    DiversityWorkbench.Settings.TimeoutConnection = _TimeoutConnection;
                    DiversityWorkbench.Settings.TimeoutWeb = _TimeoutWeb;
                    _TimeoutSettingMethod = "";
                }
                else
                {
                    // Storing the original timeouts in intermediate values and setting the timeout to the specified values provided this process is not set in parallel by another process
                    if (_TimeoutSettingMethod == Method)
                    {
                        _TimeoutDatebase = DiversityWorkbench.Settings.TimeoutDatabase;
                        _TimeoutConnection = DiversityWorkbench.Settings.TimeoutConnection;
                        _TimeoutWeb = DiversityWorkbench.Settings.TimeoutWeb;
                        DiversityWorkbench.Settings.TimeoutDatabase = DiversityWorkbench.Settings.TimeoutStart;
                        DiversityWorkbench.Settings.TimeoutConnection = DiversityWorkbench.Settings.TimeoutStart;
                        DiversityWorkbench.Settings.TimeoutWeb = 0;
                    }
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                _TimeoutSettingMethod = "";
            }
            finally
            {
                //if (IntermediateTimeout == null)
                //{
                //    if (DiversityWorkbench.Settings.TimeoutDatabase != _TimeoutDatebase)
                //        DiversityWorkbench.Settings.TimeoutDatabase = _TimeoutDatebase;
                //    if (DiversityWorkbench.Settings.TimeoutConnection != _TimeoutConnection)
                //        DiversityWorkbench.Settings.TimeoutConnection = _TimeoutConnection;
                //    if (DiversityWorkbench.Settings.TimeoutWeb != _TimeoutWeb)
                //        DiversityWorkbench.Settings.TimeoutWeb = _TimeoutWeb;
                //}
            }
        }

