        /// <summary>
        /// Initialize the map only if the map state is not DISTRIBUTION. 
        /// In latter case new localities can be added to the map and it therefore can not be reset until the state
        /// is set to a different mode
        /// </summary>
        private void initGisMap()
        {
            try
            {
                if (this._HeaderControl != HeaderControl.Maps) return;// !this.ShowImagesMaps) return;
                if (this._UserControlGIS == null)
                    this.initUserControls();
                if (this._UserControlGIS.SelectedGISObject == UserControls.UserControl_GIS.SelectedGisObject.Event)
                {
                    if (this.dataSetCollectionSpecimen.CollectionEvent.Rows.Count > 0
                        && this.dataSetCollectionSpecimen.CollectionEventLocalisationGeo.Rows.Count > 0)
                    {
                        // try to find WGS84
                        System.Data.DataRow[] rr = this.dataSetCollectionSpecimen.CollectionEventLocalisation.Select("LocalisationSystemID = 8 AND AverageLatitudeCache <> 0 AND AverageLongitudeCache <> 0");
                        int i = 0;
                        if (rr.Length > 0)
                        {
                            foreach (System.Data.DataRow R in this.dataSetCollectionSpecimen.CollectionEventLocalisation.Rows)
                            {
                                if (R["LocalisationSystemID"].ToString() == "8")
                                    break;
                                i++;
                            }
                        }
                        else // WGS84 could not be found
                            i = this.collectionEventLocalisationBindingSource.Position;

                        string LocalisationSystemID = this.dataSetCollectionSpecimen.CollectionEventLocalisation.Rows[i]["LocalisationSystemID"].ToString();
                        System.Data.DataRow[] rrGeo = this.dataSetCollectionSpecimen.CollectionEventLocalisationGeo.Select("LocalisationSystemID = " + LocalisationSystemID);
                        if (rrGeo.Length > 0)
                            this._UserControlGIS.DataRowCollectionEventLocalisationGeo = rrGeo[0];
                        else
                            this._UserControlGIS.DataRowCollectionEventLocalisationGeo = null;
                    }
                    else
                    {
                        this._UserControlGIS.DataRowCollectionEventLocalisationGeo = null;
                        //this._UserControlGIS.SelectedGISObject = UserControls.UserControl_GIS.SelectedGisObject.None;
                    }
                }
                else if (this._UserControlGIS.SelectedGISObject == UserControls.UserControl_GIS.SelectedGisObject.Series)
                {
                    if (this.dataSetCollectionSpecimen.CollectionEventSeriesGeo.Rows.Count > 0)
                        this._UserControlGIS.DataRowCollectionEventSeriesGeo = this.dataSetCollectionSpecimen.CollectionEventSeriesGeo.Rows[0];
                    else
                        this._UserControlGIS.DataRowCollectionEventSeriesGeo = null;
                }
                else if (this._UserControlGIS.SelectedGISObject == UserControls.UserControl_GIS.SelectedGisObject.Organism)
                {
                    if (this.dataSetCollectionSpecimen.IdentificationUnitGeoAnalysisGeo.Rows.Count > 0)
                    {
                        this._UserControlGIS.DataRowIdentificationUnitGeoAnalysisGeo = this.dataSetCollectionSpecimen.IdentificationUnitGeoAnalysisGeo.Rows[0];
                    }
                    else
                        this._UserControlGIS.DataRowIdentificationUnitGeoAnalysisGeo = null;
                }
                //else
                this._UserControlGIS.CollectionSpecimenID = this.ID;
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
        }

