                foreach (System.Data.DataRow R in this.DtCollection.Rows)
                {
                    if (R["CollectionID"].ToString() == CollectionID.ToString())
                    {
                        Name = R["CollectionName"].ToString();
                        break;
                    }
                }

            }
            catch (Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            return Name;
