                this._UnitLoopCheck = new Dictionary<int, int?>();
                foreach (System.Data.DataRow r in this.dataSetCollectionSpecimen.Tables["IdentificationUnit"].Rows)
                {
                    // Markus 30.4.24 - prüfung auf status
                    if (r.RowState == DataRowState.Deleted || r.RowState == DataRowState.Detached)
                        continue;

                    if (r["RelatedUnitID"].Equals(System.DBNull.Value))
                        this._UnitLoopCheck.Add(System.Int32.Parse(r["IdentificationUnitID"].ToString()), null);
                    else
                    {
                        this._UnitLoopCheck.Add(System.Int32.Parse(r["IdentificationUnitID"].ToString()), System.Int32.Parse(r["RelatedUnitID"].ToString()));
                    }
                }
                System.Collections.Generic.List<int> loops = DiversityWorkbench.Forms.FormFunctions.LoopsInHierarchy(_UnitLoopCheck);
