                        System.Data.DataRow[] rr = this.dataSetCollectionSpecimen.Tables["IdentificationUnit"].Select("IdentificationUnitID  = " + ID.ToString());
                        Loopings.Add(int.Parse(rr[0]["IdentificationUnitID"].ToString()), rr[0]["TaxonomicGroup"].ToString() + ": " + rr[0]["LastIdentificationCache"].ToString());
                    }
                    DiversityWorkbench.Forms.FormGetKeyFromDictionary f = new DiversityWorkbench.Forms.FormGetKeyFromDictionary("Loop in hierarchy", "Please select the unit that should be at the top", Loopings);
