        private int OverviewHierarchySubcollectionContentCount(System.Windows.Forms.TreeNode N, int CollectionID)
        {
            int Count = 0;
            try
            {
                string SQL = "SELECT Count(*) " + this.OverviewHierarchySubcollectionContentFromClause(N, CollectionID);
                int.TryParse(DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SQL), out Count);
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            return Count;
        }

        private string OverviewHierarchySubcollectionContentFromClause(System.Windows.Forms.TreeNode N, int CollectionID)
        {
            string SQL = "";
            try
            {
                SQL = "FROM CollectionSpecimenPart AS P INNER JOIN " +
                "CollectionSpecimen AS S ON P.CollectionSpecimenID = S.CollectionSpecimenID LEFT OUTER JOIN " +
                "CollectionEvent AS E ON S.CollectionEventID = E.CollectionEventID LEFT OUTER JOIN " +
                "IdentificationUnit AS U ON S.CollectionSpecimenID = U.CollectionSpecimenID " +
                "WHERE P.CollectionID = " + CollectionID.ToString() + " AND S.CollectionSpecimenID <> " + this.ID;
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            return SQL;
        }

