        private void addOverviewHierarchyCollectionTask(System.Windows.Forms.TreeNode N, int SpecimenPartID)
        {
            if (this.ShowCollectionTask)
            {
                try
                {
                    // try to find a collection task corresponding to the current specimen
                    System.Data.DataRow[] rr = this.dataSetCollectionSpecimen.CollectionSpecimenPart.Select("SpecimenPartID = " + SpecimenPartID.ToString(), "");
                    if (rr.Length > 0)
                    {
                        DiversityWorkbench.CollectionSpecimen S = new DiversityWorkbench.CollectionSpecimen(DiversityWorkbench.Settings.ServerConnection);
                        string URI = S.UnitValues(this.ID)["_URI"];
                        string SQL = "SELECT CollectionTaskID " +
                        "FROM CollectionTask AS C INNER JOIN Task T ON C.TaskID = T.TaskID  " +
                        " AND C.SpecimenPartID = " + SpecimenPartID.ToString();
                        System.Data.DataTable dtCollectionTaskID = new DataTable();
                        Microsoft.Data.SqlClient.SqlDataAdapter ad = new Microsoft.Data.SqlClient.SqlDataAdapter(SQL, DiversityWorkbench.Settings.ConnectionString);
                        ad.Fill(dtCollectionTaskID);
                        foreach (System.Data.DataRow Rsc in dtCollectionTaskID.Rows)
                        {
                            this.DataSetCollectionTask().Tables["CollectionTask"].Clear();
                            SQL = "select * from [dbo].[CollectionTaskChildNodes](" + Rsc[0].ToString() + ") union select * from [dbo].[CollectionTaskParentNodes](" + Rsc[0].ToString() + ")";
                            ad.SelectCommand.CommandText = SQL;
                            ad.Fill(this.DataSetCollectionTask().Tables["CollectionTask"]);
                            System.Data.DataRow[] RStart = this.DataSetCollectionTask().Tables["CollectionTask"].Select("CollectionTaskParentID is null");
                            if (RStart.Length > 0)
                            {
                                foreach (System.Data.DataRow row in RStart)
