            try
            {
                this.contextMenuStripTreeUnit.Items.Clear();
                foreach (System.Windows.Forms.ToolStripItem M in this.toolStripOverviewHierarchyEdit.Items)
                {
                    if (!M.Visible)
                        continue;
                    string x = M.GetType().ToString();
