                    {
                        System.Windows.Forms.ToolStripMenuItem T = new ToolStripMenuItem(M.ToolTipText, M.Image);
                        T.TextImageRelation = TextImageRelation.ImageBeforeText;
                        T.ImageScaling = ToolStripItemImageScaling.None;
