        private void toolStripButtonOverviewHierarchyNewIdentDependent_Click(object sender, EventArgs e)
        {
            if (this.treeViewOverviewHierarchy.SelectedNode == null)
            {
                System.Windows.Forms.MessageBox.Show("Please select an identification");
                return;
            }

            int IdentificationSequenceCurrent = 0;
            System.Data.DataRowView Rcurr = (System.Data.DataRowView)this.identificationBindingSource.Current;
            if (!int.TryParse(Rcurr["IdentificationSequence"].ToString(), out IdentificationSequenceCurrent)) return;
            if (Rcurr["TermURI"].Equals(System.DBNull.Value) || Rcurr["TermURI"].ToString().Length == 0)
            {
                System.Windows.Forms.MessageBox.Show("The identification needs to be connected to a valid source (DiversityScientificTerms)");
                return;
            }

            DiversityWorkbench.ScientificTerm ST = new DiversityWorkbench.ScientificTerm(DiversityWorkbench.Settings.ServerConnection);
            bool OK = ST.DependentQueryAvailable(Rcurr["TermURI"].ToString());
            if (OK)
            {
                System.Data.DataRow R = this.createNewIdentification(IdentInsertType.Dependent, IdentificationSequenceCurrent);
                this.refreshHierarchyUnitUnitNodes(this.treeViewOverviewHierarchy.SelectedNode);
                this.treeViewOverviewHierarchy.SelectedNode = this.getTreeNodeOfDataRow(this.treeViewOverviewHierarchy, null, R);
            }
            else
            {
                System.Windows.Forms.MessageBox.Show("No dependent terms available");
                return;
            }
        }

