                {
                    // Test if the given string is allowed
                    string SQL = "SELECT TOP 1 TransactionID FROM [Transaction] T WHERE T.TransactionTitle = '" + freg.String + "' ";// AND T.TransactionType = 'provision'";
                    int TransactionID;
                    if (int.TryParse(DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SQL), out TransactionID))
                    {
                        // Markus 4.12.2023: Testing the type of the transaction
                        SQL = "SELECT TransactionType FROM [Transaction] T WHERE T.TransactionID = " + TransactionID.ToString();
