        private System.Collections.Generic.Dictionary<string, int> _ProjectCodesChecked;

        private bool ProjectCodesChanged(LookupTable.Lookup lookup)
        {
            if (this.ID == -1) return false;

            // Check only once for every ID and table
            if (_ProjectCodesChecked == null) _ProjectCodesChecked = new Dictionary<string, int>();
            if (_ProjectCodesChecked.ContainsKey(LookupTable.LookupTable_Name(lookup)))
            {
                if (System.Math.Abs(_ProjectCodesChecked[LookupTable.LookupTable_Name(lookup)]) == this.ID)
                    return _ProjectCodesChecked[LookupTable.LookupTable_Name(lookup)] > 0;
                else
                    _ProjectCodesChecked.Remove(LookupTable.LookupTable_Name(lookup));
            }

            // Checking a new ID
            bool ProjectChanged = false;
            System.Collections.Generic.List<string> ProjectEnumOld = this.EnumProjectCodes(lookup);
            this._EnumProjectCodes.Remove(LookupTable.LookupTable_Name(lookup));
            System.Collections.Generic.List<string> ProjectEnumNew = this.EnumProjectCodes(lookup);
            if (ProjectEnumOld.Count != ProjectEnumNew.Count)
                ProjectChanged = true;
            else if (ProjectEnumNew.Count > 0)
            {
                ProjectEnumNew.Sort();
                ProjectEnumOld.Sort();
                for (int i = 0; i < ProjectEnumNew.Count; i++)
                {
                    if (ProjectEnumNew[i] != ProjectEnumOld[i])
                    {
                        ProjectChanged = true;
                        break;
                    }
                }
            }
            int id = this.ID;
            if (!ProjectChanged) id = -id;
            _ProjectCodesChecked.Add(LookupTable.LookupTable_Name(lookup), id);
            return ProjectChanged;
        }

        private System.Collections.Generic.Dictionary<string, System.Collections.Generic.List<string>> _EnumProjectCodes;

        private System.Collections.Generic.List<string> EnumProjectCodes(LookupTable.Lookup lookup)
        {
            if (_EnumProjectCodes == null)
            {
                _EnumProjectCodes = new Dictionary<string, List<string>>();
            }
            if (!_EnumProjectCodes.ContainsKey(LookupTable.LookupTable_Name(lookup)))
            {
                string Table = LookupTable.LookupTable_Name(lookup);
                System.Collections.Generic.List<string> list = DiversityWorkbench.EnumTable.ProjectAvailableCodes(LookupTable.LookupTable_Name(lookup), "CollectionProject", "CollectionSpecimenID", this.ID);
                _EnumProjectCodes.Add(LookupTable.LookupTable_Name(lookup), list);
            }
            return _EnumProjectCodes[LookupTable.LookupTable_Name(lookup)];
        }

        private bool EnumProjectUsed(LookupTable.Lookup lookup)
        {
            return EnumProjectCodes(lookup).Count > 0;
        }

        private void EnumHierarchy(ref System.Collections.Generic.Dictionary<string, string> EnumDict, LookupTable.Lookup lookup, string Parent = "", string PreviousParent = null)
        {
            try
            {
                // check if project restriction should be used
                bool UseProject = EnumProjectUsed(lookup);

                // walk through table hierarchy
                string Restriction = "ParentCode = '" + Parent + "'";
                if (Parent.Length == 0) Restriction = "ParentCode IS NULL";
                foreach (System.Data.DataRow R in LookupTable.LookupTable_Table(lookup).Select(Restriction))
                {
                    string Code = R["Code"].ToString();
                    // for parents that are not in the list
                    if (PreviousParent != null && Parent != PreviousParent)
                        Parent = PreviousParent;
                    // Check if code is in list
                    if ((UseProject && EnumProjectCodes(lookup).Contains(Code)) || (!UseProject && LookupTable.ClientSelection(lookup).Contains(Code)))
                    {
                        EnumDict.Add(Code, Parent);
                        EnumHierarchy(ref EnumDict, lookup, Code);
                    }
                    else
                    {
                        EnumHierarchy(ref EnumDict, lookup, Code, Parent);
                    }
                }
            }
            catch (System.Exception ex) { DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex); }
        }

