        public bool setSpecimenPart(int ID)
        {
            bool OK = true;
            int CollectionSpecimenID;
            string SQL = "SELECT CollectionSpecimenID FROM CollectionSpecimenPart WHERE SpecimenPartID = " + ID.ToString();
            if (int.TryParse(DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SQL, true), out CollectionSpecimenID))
            {
                this.setSpecimen(CollectionSpecimenID);
                System.Data.DataRow[] rr = this.dataSetCollectionSpecimen.Tables["CollectionSpecimenPart"].Select("SpecimenPartID = " + ID.ToString());
                if (rr.Length == 1)
                {
                    this.SelectNode(rr[0], Tree.PartTree);
                }
                else
                    OK = false;
            }
            else
            {
                OK = false;
            }
            return OK;
        }

        public int? getSpecimenPartID()
        {
            if (this.treeViewOverviewHierarchyStorage.SelectedNode != null
                && this.treeViewOverviewHierarchyStorage.SelectedNode.Tag != null
                && this.treeViewOverviewHierarchyStorage.SelectedNode.Tag.GetType() == typeof(DiversityCollection.Datasets.DataSetCollectionSpecimen.CollectionSpecimenPartRow))
            {
                System.Data.DataRow R = (System.Data.DataRow)this.treeViewOverviewHierarchyStorage.SelectedNode.Tag;
                int PartID;
                if (int.TryParse(R["SpecimenPartID"].ToString(), out PartID))
                    return PartID;
                else
                    return null;
            }
            else
                return null;
        }

        public string getSpecimenPartDisplayText()
