        #region setting the header

        private void SetHeaderControl()
        {
            string Display = DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.ImageDisplay;

            if (Display == HeaderControl.Maps.ToString()) // Maps
            {
                this.SetHeaderControl(HeaderControl.Maps, mapToolStripMenuItem.Image);
            }
            else if (Display == HeaderControl.EventImages.ToString()) // Event images
            {
                this.SetHeaderControl(HeaderControl.EventImages, eventImagesToolStripMenuItem.Image);
            }
            else if (Display == HeaderControl.SpecimenImages.ToString()) // Specimen images
            {
                this.SetHeaderControl(HeaderControl.SpecimenImages, specimenImagesToolStripMenuItem.Image);
            }
            else if (Display == HeaderControl.Printing.ToString()) // Printing
            {
                this.SetHeaderControl(HeaderControl.Printing, printingToolStripMenuItem.Image);
            }
            else if (Display == HeaderControl.SeriesImages.ToString()) // Series images
            {
                this.SetHeaderControl(HeaderControl.SeriesImages, seriesImagesToolStripMenuItem.Image);
            }
            else if (Display == HeaderControl.Plan.ToString()) // Plan
            {
                this.SetHeaderControl(HeaderControl.Plan, planToolStripMenuItem.Image);
            }
            else if (Display == HeaderControl.Hidden.ToString()) // Hiding
            {
                this.SetHeaderControl(HeaderControl.Hidden, hideToolStripMenuItem.Image);
            }
            else
            {
                this.SetHeaderControl(HeaderControl.Hidden, hideToolStripMenuItem.Image);
            }
        }

        private void SetHeaderControl(HeaderControl Control, System.Drawing.Image Image)
        {
            try
            {
                bool ChangeToOrFromHidden = false;
                if (this._HeaderControl == HeaderControl.Hidden && Control != HeaderControl.Hidden)
                    ChangeToOrFromHidden = true;
                if (this._HeaderControl != HeaderControl.Hidden && Control == HeaderControl.Hidden)
                    ChangeToOrFromHidden = true;

                // Organize specimen image restriction, i.e. if the specimen images may be restricted to parts or units
                if (Control == HeaderControl.SpecimenImages)
                {
                    this.UserControl_Unit.EnableImageRestriction(true);
                    this.UserControl_Part.EnableImageRestriction(true);
                }
                else
                {
                    this.UserControl_SpecimenImages.ReleaseImageRestriction();
                    this.UserControl_Unit.EnableImageRestriction(false);
                    this.UserControl_Part.EnableImageRestriction(false);
                }

                this.toolStripDropDownButtonHeader.Image = Image;
                this._HeaderControl = Control;
                string ImageDisplay = DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.ImageDisplay;
                if (Control == HeaderControl.Hidden)
                {
                    this.splitContainerData.Panel1Collapsed = true;
                    this.splitContainerOverviewHierarchy.SplitterDistance = (int)(this.Height / 2);
                }
                else
                {
                    this.splitContainerData.Panel1Collapsed = false;
                    this.splitContainerOverviewHierarchy.SplitterDistance = (int)(this.splitContainerOverviewHierarchy.Height / 2);
                    switch (this._HeaderControl)
                    {
                        case HeaderControl.SeriesImages:
                            this.addHeaederControl(this.UserControl_EventSeriesImages);
                            this._UserControl_EventSeriesImages.Enabled = true;
                            // if there are series available, set the selection on it to show the corresponding image
                            try
                            {
                                if (this.dataSetCollectionEventSeries.CollectionEventSeries.Rows.Count > 0)
                                {
                                    int ID = 0;
                                    if (this.SeriesID != null)
                                        ID = (int)this.SeriesID;
                                    else
                                        ID = int.Parse(this.dataSetCollectionSpecimen.CollectionEvent.Rows[0]["SeriesID"].ToString());
                                    System.Data.DataRow[] rr = this.dataSetCollectionEventSeries.CollectionEventSeries.Select("SeriesID = " + ID.ToString());
                                    if (rr.Length > 0)
                                    {
                                        bool setNode = false;
                                        if (this.SelectedUnitHierarchyNode() == null)
                                            setNode = true;
                                        if (this.SelectedUnitHierarchyNode() != null && this.SelectedUnitHierarchyNode().Tag != null)
                                        {
                                            System.Data.DataRow r = (System.Data.DataRow)this.SelectedUnitHierarchyNode().Tag;
                                            if (r.Table.TableName != "CollectionEventSeries")
                                            {
                                                setNode = true;
                                            }
                                        }
                                        if (setNode)
                                        {
                                            System.Windows.Forms.TreeNode N = this.getTreeNodeOfDataRow(this.treeViewOverviewHierarchy, null, rr[0]);
                                            this.treeViewOverviewHierarchy.SelectedNode = N;
                                        }
                                    }
                                    this.UserControl_EventSeriesImages.SetPosition();
                                }
                                else
                                {
                                    this._UserControl_EventSeriesImages.Enabled = false;
                                }
                            }
                            catch (Exception ex)
                            {
                                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                            }
                            break;
                        case HeaderControl.EventImages:
                            this.addHeaederControl(this.UserControl_EventImages);
                            // No Event available
                            if (this.dataSetCollectionSpecimen.CollectionSpecimen.Rows.Count > 0 && this.dataSetCollectionSpecimen.CollectionEvent.Rows.Count == 0)
                            {
                                this.UserControl_EventImages.Enabled = false;
                            }
                            else
                            {
                                this.UserControl_EventImages.Enabled = true;
                                // set the selection in the main tree on the event
                                if (this.dataSetCollectionSpecimen.CollectionEvent.Rows.Count > 0)
                                {
                                    try
                                    {
                                        if (this.SelectedUnitHierarchyNode() != null && this.SelectedUnitHierarchyNode().Tag != null)
                                        {
                                            System.Data.DataRow r = (System.Data.DataRow)this.SelectedUnitHierarchyNode().Tag;
                                            if (r.Table.TableName != "CollectionEvent")
                                            {
                                                System.Windows.Forms.TreeNode N = this.getTreeNodeOfDataRow(this.treeViewOverviewHierarchy, null, this.dataSetCollectionSpecimen.CollectionEvent.Rows[0]);
                                                this.treeViewOverviewHierarchy.SelectedNode = N;
                                            }
                                        }
                                    }
                                    catch (Exception ex)
                                    {
                                        DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                                    }
                                }
                            }
                            this.UserControl_EventImages.SetPosition();
                            break;
                        case HeaderControl.SpecimenImages:
                            this.addHeaederControl(this.UserControl_SpecimenImages);
                            // set the selection in the main tree on the specimen
                            try
                            {
                                if (this.SelectedUnitHierarchyNode() != null && this.SelectedUnitHierarchyNode().Tag != null)
                                {
                                    System.Data.DataRow r = (System.Data.DataRow)this.SelectedUnitHierarchyNode().Tag;
                                    if (r.Table.TableName != "CollectionSpecimen")
                                    {
                                        System.Windows.Forms.TreeNode N = this.getTreeNodeOfDataRow(this.treeViewOverviewHierarchy, null, this.dataSetCollectionSpecimen.CollectionSpecimen.Rows[0]);
                                        this.treeViewOverviewHierarchy.SelectedNode = N;
                                    }
                                }
                                else
                                {
                                    if (this.dataSetCollectionSpecimen.CollectionSpecimen.Rows.Count > 0)
                                    {
                                        System.Windows.Forms.TreeNode N = this.getTreeNodeOfDataRow(this.treeViewOverviewHierarchy, null, this.dataSetCollectionSpecimen.CollectionSpecimen.Rows[0]);
                                        this.treeViewOverviewHierarchy.SelectedNode = N;
                                    }
                                    else
                                    {
                                        if (this.treeViewOverviewHierarchy.Nodes.Count > 0)
                                            this.treeViewOverviewHierarchy.SelectedNode = this.treeViewOverviewHierarchy.Nodes[0];
                                    }
                                }
                            }
                            catch (Exception ex)
                            {
                                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                            }
                            break;
                        case HeaderControl.Maps:
                            this.addHeaederControl(this._UserControlGIS);
                            break;
                        case HeaderControl.Printing:
                            this.addHeaederControl(this.UserControl_Print);
                            break;
                        case HeaderControl.Plan:
                            this.addHeaederControl(this.UserControl_Plan);
                            break;
                    }
                }
                DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.ImageDisplay = Control.ToString();
                DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.Save();
                this.refreshHeader();
                if (ChangeToOrFromHidden && false)
                {
                    if (this.treeViewOverviewHierarchy.SelectedNode != null)
                        this.setControls(this.treeViewOverviewHierarchy.SelectedNode);
                    else if (this.treeViewOverviewHierarchyStorage.SelectedNode != null)
                        this.setControls(this.treeViewOverviewHierarchyStorage.SelectedNode);
                    else
                        this.setControls();
                }
            }
            catch (Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
        }

