        private void refreshHeader()
        {
            try
            {
                switch (this._HeaderControl)
                {
                    case HeaderControl.Printing:
                        this.UserControl_Print.ResetLabel();
                        this.UserControl_Print.setMultiPrintRestrictionText();// .setMultiPrintRestrictionText();
                        break;
                    case HeaderControl.EventImages:
                        // No restriction as only 1 event
                        this.UserControl_EventImages.ShowImages();
                        this.UserControl_EventImages.setAvailability();
                        if (this.dataSetCollectionSpecimen.CollectionEvent.Rows.Count > 0)
                            this.UserControl_EventImages.Enabled = true;
                        else
                            this.UserControl_EventImages.Enabled = false;
                        break;
                    case HeaderControl.SeriesImages:
                        if (this.treeViewOverviewHierarchy.SelectedNode != null && this.treeViewOverviewHierarchy.SelectedNode.Tag != null)
                        {
                            System.Data.DataRow R = (System.Data.DataRow)this.treeViewOverviewHierarchy.SelectedNode.Tag;
                            if (R.Table.TableName == "CollectionEventSeries")// && this.dataSetCollectionEventSeries.CollectionEventSeriesImage.Rows.Count > 0)
                            {
                                int? SeriesID = int.Parse(R["SeriesID"].ToString());// null;--8274
                                this.UserControl_EventSeriesImages.ShowImages(SeriesID);
                                this.UserControl_EventSeriesImages.setAvailability();
                            }
                        }
                        else if (this.dataSetCollectionEventSeries.CollectionEventSeriesImage.Rows.Count > 0)
                        {
                            bool Fit = false;
                            System.Data.DataRow RS = this.dataSetCollectionEventSeries.CollectionEventSeriesImage.Rows[0];
                            int SeriesID = 0;
                            int.TryParse(RS["SeriesID"].ToString(), out SeriesID);// null;--8274
                            if (this.dataSetCollectionSpecimen.CollectionEventSeries.Rows.Count > 0)
                            {
                                if (RS["SeriesID"].ToString() == this.dataSetCollectionSpecimen.CollectionEventSeries.Rows[0]["SeriesID"].ToString())
                                {
                                    Fit = true;
                                }
                                else
                                {
                                    int.TryParse(this.dataSetCollectionSpecimen.CollectionEventSeries.Rows[0]["SeriesID"].ToString(), out SeriesID);
                                    Fit = true;
                                }
                            }
                            if (Fit)
                            {
                                this.UserControl_EventSeriesImages.ShowImages(SeriesID);
                            }
                            else
                            {
                                this.UserControl_EventSeriesImages.ShowImages(null);
                            }
                            this.UserControl_EventSeriesImages.setAvailability();
                        }
                        else
                        {
                            this.UserControl_EventSeriesImages.ShowImages(null);
                            this.UserControl_EventSeriesImages.setAvailability();
                        }
                        break;
                    case HeaderControl.SpecimenImages:
                        this.ReleaseImageRestriction();
                        // No restriction as only 1 specimen
                        this.UserControl_SpecimenImages.ShowImages();
                        this.UserControl_SpecimenImages.setAvailability();
                        break;
                    case HeaderControl.Plan:
                        int? CollectionID = null;
                        if (this.treeViewOverviewHierarchyStorage.SelectedNode != null && this.treeViewOverviewHierarchyStorage.SelectedNode.Tag != null)
                        {
                            System.Data.DataRow row = (System.Data.DataRow)this.treeViewOverviewHierarchyStorage.SelectedNode.Tag;
                            if (row.Table.Columns.Contains("CollectionID"))
                            {
                                if (!row["CollectionID"].Equals(System.DBNull.Value))
                                {
                                    try
                                    {
                                        int ID;
                                        if (int.TryParse(row["CollectionID"].ToString(), out ID))
                                        {
                                            CollectionID = ID;
                                        }
                                    }
                                    catch (System.Exception ex)
                                    {
                                        DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                                    }
                                }
                            }
                        }
                        else if (this.dataSetCollectionSpecimen.CollectionSpecimenPart.Rows.Count == 1)
                        {
                            System.Data.DataRow row = this.dataSetCollectionSpecimen.CollectionSpecimenPart.Rows[0];
                            if (row.Table.Columns.Contains("CollectionID"))
                            {
                                if (!row["CollectionID"].Equals(System.DBNull.Value))
                                {
                                    try
                                    {
                                        int ID;
                                        if (int.TryParse(row["CollectionID"].ToString(), out ID))
                                        {
                                            CollectionID = ID;
                                        }
                                    }
                                    catch (System.Exception ex)
                                    {
                                        DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
                                    }
                                }
                            }
                        }
                        else if (this.dataSetCollectionSpecimen.CollectionSpecimenPart.Rows.Count > 1)
                        {
                            System.Collections.Generic.List<int> IDs = new List<int>();
                            foreach (System.Data.DataRow R in this.dataSetCollectionSpecimen.CollectionSpecimenPart.Rows)
                            {
                                int ID;
                                if (int.TryParse(R["CollectionID"].ToString(), out ID))
                                {
                                    if (!IDs.Contains(ID)) IDs.Add(ID);
                                }
                            }
                            if (IDs.Count == 1)
                                CollectionID = IDs[0];
                        }
                        if (CollectionID != null)
                            this.UserControl_Plan.SetCollectionID((int)CollectionID);
                        else
                            this.UserControl_Plan.SetCollectionID(-1);


                        //bool NoPlan = true;
                        //if (this.treeViewOverviewHierarchyStorage.SelectedNode != null && this.treeViewOverviewHierarchyStorage.SelectedNode.Tag != null)
                        //{
                        //    System.Data.DataRow row = (System.Data.DataRow)this.treeViewOverviewHierarchyStorage.SelectedNode.Tag;
                        //    if (row.Table.Columns.Contains("CollectionID"))
