        private void TargetSplitterAdd(DataTarget Target, string[] tt)
        {
            try
            {
                string[] HH = tt[1].Split(new char[] { '|' });
                if (HH.Length == 3)
                {
                    System.Collections.Generic.List<int> L = new List<int>();
                    L.Add(int.Parse(HH[0]));
                    L.Add(int.Parse(HH[1]));
                    L.Add(int.Parse(HH[2]));
                    this.TargetSplitter.Add(Target, L);
                }
            }
            catch { }

        }

