        private int TargetHeight(DataTarget Target)
        {
            int Height = this._TargetDefaultHeight;
            switch (Target)
            {
                case DataTarget.Event:
                    Height = 220;
                    break;
                case DataTarget.EventLocalisation:
                    Height = 150;
                    break;
                case DataTarget.EventProperty:
                    Height = 140;
                    break;
                case DataTarget.EventSeries:
                    Height = 220;
                    break;
                case DataTarget.Specimen:
                    if (DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.ShowExsiccata)
                        Height = 178;
                    else
                        Height = 156;// 116;
                    break;
                case DataTarget.SpecimenExsiccata:
                    Height = 24;
                    break;
                case DataTarget.SpecimenLabel:
                    Height = 90;
                    break;
                case DataTarget.Project:
                    Height = 120;
                    break;
                case DataTarget.Analysis:
                    Height = 170;
                    break;
                case DataTarget.Unit:
                    Height = this.TargetHeights()[DataTarget.Unit];// 226;
                    if (DiversityCollection.Forms.FormCollectionSpecimenSettings.Default.ShowExsiccata)
                        Height += 28;
                    //else
                    //    Height = 206;
                    break;
                case DataTarget.UnitDisplayOrder:
                    Height = 100;
                    break;
                case DataTarget.Identification:
                    Height = 160;
                    break;
                case DataTarget.Part:
                    Height = 166;
                    break;
                case DataTarget.PartDescription:
                    Height = 150;
                    break;
                case DataTarget.PartDisplayOrder:
                    Height = 130;
                    break;
            }
            Height = (int)((float)Height * DiversityWorkbench.Forms.FormFunctions.DisplayZoomFactor);
            return Height;
        }

