        private void setControls(System.Windows.Forms.TreeNode Node = null)// System.Data.DataRow R = null)
        {
            this.Cursor = System.Windows.Forms.Cursors.WaitCursor;
            this.SuspendLayout();
            this.splitContainerDataDetails.SuspendLayout();
            this.splitContainerDataDetails_1.SuspendLayout();
            this.splitContainerDataDetails_1.Panel1.SuspendLayout();
            this.splitContainerDataDetails_1.Panel2.SuspendLayout();
            this.splitContainerDataDetails_2.SuspendLayout();
            this.splitContainerDataDetails_2.Panel1.SuspendLayout();
            this.splitContainerDataDetails_2.Panel2.SuspendLayout();
            this.refreshHeader();
            //this._EnumProjectCodes = null;
            //bool ProjectTaxaChanged = false;
            //bool ProjectMaterialChanged = false;
            try
            {
                System.Data.DataRow R = null;
                if (Node != null)
                {
                    while (R == null)
                    {
                        if (Node.Tag != null)
                        {
                            R = (System.Data.DataRow)Node.Tag;
                        }
                        else if (Node.Parent != null)
                        {
                            Node = Node.Parent;
                            if (Node.Tag != null)
                            {
                                R = (System.Data.DataRow)Node.Tag;
                            }
                        }
                        else
                            break;
                    }
                }
                int Position = 0;
                if (R != null)
                {
                    this._DataTable = R.Table.TableName;
                    foreach (System.Data.DataRow rr in R.Table.Rows)
                    {
                        if (rr == R)
                            break;
                        Position++;
                    }
                }
                else
                    this._DataTable = "";

                System.Collections.Generic.List<System.Drawing.Color> Colors = new List<Color>();
                System.Collections.Generic.List<DataTarget> Targets = new List<DataTarget>();
                int? MainControl = null; // if the height for a certain control should be ensured
                switch (this._DataTable)
                {
                    case "":
                        //this.setUserControls(Targets);
                        this.setUserControls(DataTarget.None, Targets);
                        break;
                    case "CollectionEventSeries":
                        Targets.Add(DataTarget.EventSeries);//, null);
                        Targets.Add(DataTarget.EventSeriesDescriptor);//, null);
                        // try to get ID
                        int SeriesID;
                        if (int.TryParse(R[0].ToString(), out SeriesID))
