        private bool IsPermit(System.Data.DataRow R)
        {
            bool ispermit = false;

            try
            {
                string SQL = "SELECT COUNT(*) FROM [TransactionPermit] where TransactionID = " + R["TransactionID"].ToString();
                string Result = DiversityWorkbench.Forms.FormFunctions.SqlExecuteScalar(SQL);
                if (Result == "1")
                    ispermit = true;
            }
            catch (Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
            return ispermit;
        }

        private void setGisControl(DiversityCollection.UserControls.UserControlGIS.SelectedGisObject Object)
        {
            System.Data.DataRow R = (System.Data.DataRow)this.treeViewOverviewHierarchy.SelectedNode.Tag;
            switch (Object)
            {
                case UserControls.UserControlGIS.SelectedGisObject.Series:
                    try
                    {
                        this.dataSetCollectionSpecimen.CollectionEventSeriesGeo.Clear();
                        string SQL = "SELECT SeriesID, Geography.ToString() AS GeographyAsString, Geography.EnvelopeCenter().ToString() AS GeographyEnvelopeCenter, " +
                            "SeriesCode, Description " +
                            "FROM CollectionEventSeries WHERE SeriesID = " + R["SeriesID"].ToString() + " AND NOT Geography IS NULL";
                        Microsoft.Data.SqlClient.SqlDataAdapter ad = new Microsoft.Data.SqlClient.SqlDataAdapter(SQL, DiversityWorkbench.Settings.ConnectionString);
                        ad.Fill(this.dataSetCollectionSpecimen.CollectionEventSeriesGeo);
                        if (this.dataSetCollectionSpecimen.CollectionEventSeriesGeo.Rows.Count > 0)
                            this._UserControlGIS.DataRowCollectionEventSeriesGeo = this.dataSetCollectionSpecimen.CollectionEventSeriesGeo.Rows[0];
                        else
                            this._UserControlGIS.DataRowCollectionEventSeriesGeo = null;
                    }
                    catch (System.Exception ex) { }
                    this._UserControlGIS.SelectedGISObject = UserControls.UserControl_GIS.SelectedGisObject.Series;
                    break;
                case UserControls.UserControlGIS.SelectedGisObject.Event:
                    if (this.collectionEventLocalisationBindingSource.Current != null)
