        private void setUserControls(DataTarget MainTarget, System.Collections.Generic.List<DataTarget> DataTargets, System.Windows.Forms.Control Control = null, System.Collections.Generic.List<System.Drawing.Color> Colors = null)
        {
            try
            {
                if (this._PreviousTarget != DataTarget.None)
                {
                    this.TargetSplitterAdd(this._PreviousTarget);
                }
                this._PreviousTarget = MainTarget;

                int iObjects = DataTargets.Count;

                DataTarget T1 = DataTarget.None;
                int H1 = this._TargetDefaultHeight;
                DataTarget T2 = DataTarget.None;
                int H2 = this._TargetDefaultHeight;
                DataTarget T3 = DataTarget.None;
                int H3 = this._TargetDefaultHeight;
                DataTarget T4 = DataTarget.None;
                int H4 = this._TargetDefaultHeight;

                int i = 0;
                System.Collections.Generic.List<int> Heights = new List<int>();
                foreach (DataTarget T in DataTargets)
                {
                    i++;
                    switch (i)
                    {
                        case 1:
                            T1 = T;
                            H1 = this.TargetHeight(T);
                            Heights.Add(H1);
                            break;
                        case 2:
                            T2 = T;
                            H2 = this.TargetHeight(T);
                            Heights.Add(H2);
                            break;
                        case 3:
                            T3 = T;
                            H3 = this.TargetHeight(T);
                            Heights.Add(H3);
                            break;
                        case 4:
                            T4 = T;
                            H4 = this.TargetHeight(T);
                            Heights.Add(H4);
                            break;
                    }
                }
                if (Control != null)
                {
                    if (Control.MinimumSize.Height > 0)
                        Heights.Add(Control.MinimumSize.Height);
                    else
                        Heights.Add(this._TargetDefaultHeight);
                }

                this.splitContainerDataDetails_1.Panel1.Controls.Clear();
                this.splitContainerDataDetails_1.Panel2.Controls.Clear();
                this.splitContainerDataDetails_2.Panel1.Controls.Clear();
                this.splitContainerDataDetails_2.Panel2.Controls.Clear();
                try
                {
                    if (Control != null)
                        iObjects++;
                    switch (iObjects)
                    {
                        case 1:
                            if (DataTargets.Count > 0)
                            {
                                // Target
                                this.splitContainerDataDetails_1.Panel1.Controls.Add(this.getUserControl(T1));
                                this.getUserControl(T1).Dock = DockStyle.Fill;
                                if (Colors == null)
                                    this.getUserControl(T1).SetBackgroundColor(System.Drawing.Color.White);
                                else if (Colors.Count > 0)
                                    this.getUserControl(T1).SetBackgroundColor(Colors[0]);
                            }
                            else if (Control != null)
                            {
                                // Control
                                this.splitContainerDataDetails_1.Panel1.Controls.Add(Control);
                                Control.Dock = DockStyle.Fill;
                                if (Colors == null)
                                    Control.BackColor = System.Drawing.Color.White;
                                else if (Colors.Count > 0)
                                    Control.BackColor = Colors[0];
                            }
                            break;
                        case 2:
                            // Target
                            this.splitContainerDataDetails_1.Panel1.Controls.Add(this.getUserControl(T1));
                            this.getUserControl(T1).Dock = DockStyle.Fill;
                            this.getUserControl(T1).SetBackgroundColor(System.Drawing.Color.White);
                            if (DataTargets.Count > 1)
                            {
                                // Target 2
                                this.splitContainerDataDetails_1.Panel2.Controls.Add(this.getUserControl(T2));
                                this.getUserControl(T2).Dock = DockStyle.Fill;
                                if (Colors == null)
                                    this.getUserControl(T2).SetBackgroundColor(System.Drawing.Color.White);
                                else if (Colors.Count > 0)
                                {
                                    this.getUserControl(T1).SetBackgroundColor(Colors[0]);
                                    if (Colors.Count > 1)
                                        this.getUserControl(T2).SetBackgroundColor(Colors[1]);
                                }
                            }
                            else if (Control != null)
                            {
                                // control
                                this.splitContainerDataDetails_1.Panel2.Controls.Add(Control);
                                Control.Dock = DockStyle.Fill;
                            }
                            break;
                        case 3:
                            // Target
                            this.splitContainerDataDetails_1.Panel1.Controls.Add(this.getUserControl(T1));
                            this.getUserControl(T1).Dock = DockStyle.Fill;
                            if (Colors == null)
                                this.getUserControl(T1).SetBackgroundColor(System.Drawing.SystemColors.Control);
                            else if (Colors.Count > 0)
                                this.getUserControl(T1).SetBackgroundColor(Colors[0]);

                            // Target 2
                            this.splitContainerDataDetails_1.Panel2.Controls.Add(this.getUserControl(T2));
                            this.getUserControl(T2).Dock = DockStyle.Fill;
                            if (Colors == null)
                                this.getUserControl(T2).SetBackgroundColor(System.Drawing.Color.White);
                            else if (Colors.Count > 1)
                                this.getUserControl(T2).SetBackgroundColor(Colors[1]);

