                                this.getUserControl(T3).Dock = DockStyle.Fill;
                                if (Colors == null)
                                    this.getUserControl(T3).SetBackgroundColor(System.Drawing.Color.White);
                                else if (Colors.Count > 2)
                                    this.getUserControl(T3).SetBackgroundColor(Colors[2]);
