        private void setHeights(DataTarget Target, System.Collections.Generic.List<int> Heights)
        {
            try
            {
                switch (Heights.Count)
                {
                    case 0:
                    case 1:
                        this.splitContainerDataDetails.Panel1Collapsed = false;
                        this.splitContainerDataDetails.Panel2Collapsed = true;
                        this.splitContainerDataDetails_1.Panel1Collapsed = false;
                        this.splitContainerDataDetails_1.Panel2Collapsed = true;
                        break;
                    case 2:
                        this.splitContainerDataDetails.Panel1Collapsed = false;
                        this.splitContainerDataDetails.Panel2Collapsed = true;
                        this.splitContainerDataDetails_1.Panel1Collapsed = false;
                        this.splitContainerDataDetails_1.Panel2Collapsed = false;
                        break;
                    case 3:
                        this.splitContainerDataDetails.Panel1Collapsed = false;
                        this.splitContainerDataDetails.Panel2Collapsed = false;
                        this.splitContainerDataDetails_1.Panel1Collapsed = false;
                        this.splitContainerDataDetails_1.Panel2Collapsed = false;
                        this.splitContainerDataDetails_2.Panel1Collapsed = false;
                        this.splitContainerDataDetails_2.Panel2Collapsed = true;
                        break;
                    case 4:
                        this.splitContainerDataDetails.Panel1Collapsed = false;
                        this.splitContainerDataDetails.Panel2Collapsed = false;
                        this.splitContainerDataDetails_1.Panel1Collapsed = false;
                        this.splitContainerDataDetails_1.Panel2Collapsed = false;
                        this.splitContainerDataDetails_2.Panel1Collapsed = false;
                        this.splitContainerDataDetails_2.Panel2Collapsed = false;
                        break;
                }
                if (this.TargetSplitter.ContainsKey(Target))
                {
                    this.splitContainerDataDetails.SplitterDistance = this.TargetSplitter[Target][0];
                    this.splitContainerDataDetails_1.SplitterDistance = this.TargetSplitter[Target][1];
                    this.splitContainerDataDetails_2.SplitterDistance = this.TargetSplitter[Target][2];
                }
                else
                {
                    if (Heights.Count > 1)
                        this.splitContainerDataDetails.SplitterDistance = Heights[0] + this.splitContainerDataDetails_1.SplitterWidth + Heights[1];
                    if (Heights.Count > 0)
                        this.splitContainerDataDetails_1.SplitterDistance = Heights[0];
                    if (Heights.Count > 2)
                        this.splitContainerDataDetails_2.SplitterDistance = Heights[2];
                }
            }
            catch (System.Exception ex)
            {
                DiversityWorkbench.ExceptionHandling.WriteToErrorLogFile(ex);
            }
        }

        /// <summary>
        /// 
        /// </summary>
        /// <param name="Heights">The list of the heights of the controls</param>
        /// <param name="Main">optional position of main control in 0 based list</param>
        private void setHeights(System.Collections.Generic.List<int> Heights, int? Main = null)
        {
            switch (Heights.Count)
            {
                case 0:
                case 1:
                    this.splitContainerDataDetails.Panel1Collapsed = false;
                    this.splitContainerDataDetails.Panel2Collapsed = true;
                    this.splitContainerDataDetails_1.Panel1Collapsed = false;
                    this.splitContainerDataDetails_1.Panel2Collapsed = true;
                    break;
                case 2:
                    this.splitContainerDataDetails.Panel1Collapsed = false;
                    this.splitContainerDataDetails.Panel2Collapsed = true;
                    this.splitContainerDataDetails_1.Panel1Collapsed = false;
                    this.splitContainerDataDetails_1.Panel2Collapsed = false;
                    if (Main == null)
                        this.splitContainerDataDetails_1.SplitterDistance = Heights[0];
                    else if (Main == 1)
                        this.splitContainerDataDetails_1.SplitterDistance = this.splitContainerDataDetails_1.Height - Heights[1];
                    break;
                case 3:
                    this.splitContainerDataDetails.Panel1Collapsed = false;
                    this.splitContainerDataDetails.Panel2Collapsed = false;
                    this.splitContainerDataDetails_1.Panel1Collapsed = false;
                    this.splitContainerDataDetails_1.Panel2Collapsed = false;
                    this.splitContainerDataDetails_2.Panel1Collapsed = false;
                    this.splitContainerDataDetails_2.Panel2Collapsed = true;

                    this.splitContainerDataDetails.SplitterDistance = Heights[0] + this.splitContainerDataDetails_1.SplitterWidth + Heights[1];
