        private DiversityCollection.UserControls.UserControl__Data getUserControl(DataTarget Target)
        {
            this.initUserControls();
            switch (Target)
            {
                // Series
                case DataTarget.EventSeries:
                    return this._UserControl_EventSeries;
                case DataTarget.EventSeriesDescriptor:
                    return this._UserControl_EventSeriesDescriptor;

                // Event
                case DataTarget.Event:
                    return this._UserControl_Event;
                case DataTarget.EventMethod:
                    return this._UserControl_EventMethod;
                case DataTarget.EventProperty:
                    return this._UserControl_EventProperty;
                case DataTarget.EventLocalisation:
                    return this._UserControl_EventLocality;
                // Markus 4.12.2023: Umbau Regulation
                case DataTarget.EventRegulation:
                    return this._UserControl_EventRegulation;

                // Specimen
                case DataTarget.Specimen:
                    return this._UserControl_Specimen;
                case DataTarget.SpecimenExsiccata:
                    return this._UserControl_SpecimenExsiccata;
                case DataTarget.Project:
                    return this._UserControl_ProjectsNotes;
                case DataTarget.Reference:
                    return this._UserControl_Reference;
                case DataTarget.SpecimenLabel:
                    return this._UserControl_Label;

                // Relation
                case DataTarget.Relation:
                    return this._UserControl_Relation;
                case DataTarget.RelationInvers:
                    return this._UserControl_RelationInvers;

                // Colletor
                case DataTarget.Collector:
                    return this._UserControl_Collector;

                // Unit
                case DataTarget.Analysis:
                    return this._UserControl_Analysis;
                case DataTarget.AnalysisMethod:
                    return this._UserControl_AnalysisMethod;
                case DataTarget.Unit:
                    return this._UserControl_Unit;
                case DataTarget.Identification:
                    return this._UserControl_Identification;
                case DataTarget.GeoAnalysis:
                    return this._UserControl_GeoAnalysis;
                case DataTarget.UnitDisplayOrder:
                    return this._UserControl_Unit_DisplayOrder;
                //return this._UserControl_DisplayOrderUnit;

                // Part
                case DataTarget.Part:
                    return this._UserControl_Part;
                case DataTarget.PartDisplayOrder:
                    return this._UserControl_DisplayOrder;
                case DataTarget.Processing:
                    return this._UserControl_Processing;
                case DataTarget.ProcessingMethod:
                    return this._UserControl_ProcessingMethod;
                case DataTarget.PartDescription:
                    return this._UserControl_PartDescription;
                case DataTarget.Transaction:
                    return this._UserControl_Transaction;
                case DataTarget.Permit:
                    return this._UserControl_Permit;
                case DataTarget.Collection:
                    return this._UserControl_Collection;

                case DataTarget.CollectionTask:
                    return this._UserControl_CollectionTask;

                    //case DataTarget.ScanDiversityDescriptions:
                    //    return this._UserControlModuleLinks_ScanDD;
            }
            DiversityCollection.UserControls.UserControl__Data UC = null;
            return UC;
        }

