SET @Setting = (SELECT T.'+ @Column + ' FROM ' + @Table + ' AS T WHERE ' + @WhereClause + ');
set @Setting.modify(''insert attribute ' + @Attribute + '{"' + @Value + '"} into (' + @Path + ')[1];
update T set T.' + @Column + ' = @Setting 
FROM ' + @Table + ' AS T  
WHERE ' + @WhereClause)

*/
BEGIN 
declare @SQL nvarchar(max)
