	DECLARE @Setting xml;
	SET @Setting = (SELECT T.'+ @Column + ' FROM ' + @Table + ' AS T WHERE ' + @WhereClause + ');
	set @Setting.modify(''replace value of (' + @Path + '/@' + @Attribute + ')[1] with "' + @Value + '"'');
	update T set T.' + @Column + ' = @Setting 
	FROM ' + @Table + ' AS T  
	WHERE ' + @WhereClause)
	begin try
	exec sp_executesql @SQL
	end try
	begin catch
	end catch
END

